if ( existing.getType() != null )
            {
                // If it had a different type throw error.
                // This allows declaring the same var again, but not with
                // a different (even if assignable) type.
                if ( existing.getType() != type )
                {
                    throw new UtilEvalError( "Typed variable: "+name
                        +" was previously declared with type: "
                        + existing.getType() );
                } else
                {
                    // else set it and return
                    existing.setValue( value, Variable.DECLARATION );
                    return;
                }
            }

if ( existing != null )
        {
            // Is it typed?
            if ( existing.getType() != null )
            {
                // If it had a different type throw error.
                // This allows declaring the same var again, but not with
                // a different (even if assignable) type.
                if ( existing.getType() != type )
                {
                    throw new UtilEvalError( "Typed variable: "+name
                        +" was previously declared with type: "
                        + existing.getType() );
                } else
                {
                    // else set it and return
                    existing.setValue( value, Variable.DECLARATION );
                    return;
                }
            }
            // Careful here:
            // else fall through to override and install the new typed version
        }

if(Character.isWhitespace(ch))
				{
					if(eatWhitespace)
					{
						type = WHITESPACE;
						break;
					}
					else
						return i;
				}
				else if(Character.isLetterOrDigit(ch) ||
					noWordSep.indexOf(ch) != -1)
				{
					return i;
				}
				else
				{
					break;
				}

if(Character.isWhitespace(ch))
				{
					if(eatWhitespace && !eatOnlyAfterWord)
					{
						type = WHITESPACE;
						break;
					}
					else
						return i + 1;
				}
				else if(Character.isLetterOrDigit(ch) ||
					noWordSep.indexOf(ch) != -1)
				{
					return i + 1;
				}
				else
				{
					break;
				}

if(input != '\0')
			{
				if (!dryRun)
				{
					setCurrentBindings(bindings);
					invokeReadNextChar(input);
					repeatCount = 1;
				}
				return true;
			}
			else
			{
				if (!dryRun)
				{
					readNextChar = null;
					view.getStatus().setMessage(null);
				}
			}

if(readNextChar != null)
		{
			if(input != '\0')
			{
				if (!dryRun)
				{
					setCurrentBindings(bindings);
					invokeReadNextChar(input);
					repeatCount = 1;
				}
				return true;
			}
			else
			{
				if (!dryRun)
				{
					readNextChar = null;
					view.getStatus().setMessage(null);
				}
			}
		}

